let base_url="http://localhost:3000",btnSignin=document.querySelector("#btnSignin").addEventListener("click",()=>{let e=document.querySelector("#email").value.trim(),t=document.querySelector("#password").value.trim();fetch(base_url+"/users/login",{method:"post",headers:{"Content-Type":"application/json"},body:JSON.stringify({username:e,password:t})}).then(e=>e.json()).then(e=>{if("success"===e.status){let t=e.data.token;localStorage.setItem("token",t),window.location.href="/"}else document.querySelector(".info").classList.add("red"),document.querySelector(".info").classList.remove("green"),document.querySelector(".info--text").innerHTML="Login failed"})});